import React, { useState } from 'react';

function Captcha() {
  const [generatedCaptcha, setGeneratedCaptcha] = useState('');
  const [userInput, setUserInput] = useState('');
  const [isValid, setIsValid] = useState(null);

  // Function to generate a random CAPTCHA
  const generateRandomCaptcha = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let captcha = '';
    for (let i = 0; i < 6; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      captcha += characters.charAt(randomIndex);
    }
    setGeneratedCaptcha(captcha);
  };

  // Function to handle user input and validate the CAPTCHA
  const handleUserInput = () => {
    if (userInput === generatedCaptcha) {
      setIsValid(true);
    } else {
      setIsValid(false);
    }
  };

  return (
    <div className="captcha-container">
      <h2>Random CAPTCHA Generator</h2>
      <button onClick={generateRandomCaptcha}>Generate CAPTCHA</button>
      <div className="captcha">
        <span className={isValid === true ? 'valid' : isValid === false ? 'invalid' : ''}>
          {generatedCaptcha}
        </span>
      </div>
      <input
        type="text"
        placeholder="Enter CAPTCHA"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />
      <button onClick={handleUserInput}>Validate</button>
      {isValid === true && <p className="valid-message">CAPTCHA is valid!</p>}
      {isValid === false && <p className="invalid-message">CAPTCHA is invalid.</p>}
    </div>
  );
}

export default Captcha;
